    public void repintar(int aceleracion, double _consumo){
        
        barraAcelerador.setValue(aceleracion);
        barraConsumo.setValue((int) Math.round(_consumo));
        if(_consumo == 0)
            consumo.setText("N/A");
        else{
            NumberFormat nf = NumberFormat.getInstance();
            nf.setMaximumFractionDigits(4);
            String st = nf.format(_consumo);
            consumo.setText(st);
            
        }
    }

